mapper.select:
SELECT 

FROM exam a

WHERE a.id = #{id}

SELECT 

FROM exam a




		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.score = #{score}


		AND a.pass_flag = #{passFlag}


		AND a.pay_flag = #{payFlag}


		AND a.exam_count = #{examCount}




		ORDER BY ${page.orderBy}

SELECT 

FROM exam a






		ORDER BY ${page.orderBy}
